import React from 'react';
import { render } from 'react-dom';
import CardContainer from './CardContainer/CardContainer';
import CitySearch from './CitySearch/CitySearch';



const cityUrl = 'http://localhost:3000/cities'

class Home extends React.Component {

    componentDidMount=()=>{
        navigator.geolocation.getCurrentPosition(function(position) {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
          });
          var unirest = require("unirest");

          var req = unirest("GET", "https://hotels-com-provider.p.rapidapi.com/v1/hotels/nearby");

          req.query({
              "locale": "en_US",
              "sort_order": "STAR_RATING_HIGHEST_FIRST",
              "latitude": "50.073658",
              "longitude": "14.418540",
              "adults_number": "1",
              "checkin_date": "2022-03-26",
              "currency": "USD",
              "checkout_date": "2022-03-27",
              "guest_rating_min": "4",
              "page_number": "1",
              "price_max": "500",
              "amenity_ids": "527,2063",
              "price_min": "10",
              "children_ages": "4,0,15",
              "accommodation_ids": "20,8,15,5,1",
              "star_rating_ids": "3,4,5",
              "theme_ids": "14,27,25"
          });

          req.headers({
              "x-rapidapi-key": "8757bf3de4mshfa1b1b6020f8780p15b4b3jsn7efd90a3baa0",
              "x-rapidapi-host": "hotels-com-provider.p.rapidapi.com",
              "useQueryString": true
          });


          req.end(function (res) {
              if (res.error) throw new Error(res.error);

              console.log(res.body);
          });
    }



  render() {
    return (
      <div>
          <h1>Welcome Home:</h1>
          <CitySearch/>
      </div>
    );
  }
}
export default Home
