    }





  render() {
    return (
      <div>
          <h1>Welcome Home:</h1>
          <CitySearch searchHandle={this.searchHandle}
          handleChange={this.handleChange}
          weather={this.state.weather}/>

      </div>
    );
  }
}
export default Home
